import React, { useState, useEffect } from 'react';
import quizData from '../data/quiz.json';

type Question = {
  id: number;
  question: string;
  options: string[];
  correctAnswer: string;
};

const Quiz: React.FC = () => {
  const [questions, setQuestions] = useState<Question[]>([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState<string | null>(null);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [answered, setAnswered] = useState(false);
  
  useEffect(() => {
    // Load and shuffle quiz data
    const shuffledQuestions = [...quizData].sort(() => Math.random() - 0.5).slice(0, 10);
    setQuestions(shuffledQuestions);
  }, []);
  
  const handleOptionSelect = (option: string) => {
    if (answered) return;
    
    setSelectedOption(option);
    setAnswered(true);
    
    // Check if answer is correct
    if (option === questions[currentQuestionIndex].correctAnswer) {
      setScore(score + 1);
    }
  };
  
  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedOption(null);
      setAnswered(false);
    } else {
      setShowResult(true);
    }
  };
  
  const resetQuiz = () => {
    // Reshuffle questions for a new quiz
    const shuffledQuestions = [...quizData].sort(() => Math.random() - 0.5).slice(0, 10);
    setQuestions(shuffledQuestions);
    setCurrentQuestionIndex(0);
    setSelectedOption(null);
    setScore(0);
    setShowResult(false);
    setAnswered(false);
  };
  
  if (questions.length === 0) {
    return <div className="text-center py-10 font-body">Loading quiz...</div>;
  }
  
  if (showResult) {
    return (
      <div className="container mx-auto px-4 pt-8 pb-20">
        <div className="leaf-border nature-bg p-8 rounded-lg shadow-natural max-w-md mx-auto">
          <h1 className="text-3xl font-heading text-center text-primary mb-6">Quiz Results</h1>
          
          <div className="text-center mb-8">
            <div className="text-7xl font-heading mb-4 text-forest-dark">
              {score}/{questions.length}
            </div>
            <div className="h-4 bg-gray-200 rounded-full overflow-hidden mb-4">
              <div 
                className="h-full bg-primary rounded-full transition-all duration-1000"
                style={{ width: `${(score / questions.length) * 100}%` }}
              ></div>
            </div>
            <p className="font-body text-earth-dark">
              {score === questions.length
                ? 'Perfect score! You\'re a plant expert! üåøüëë'
                : score >= questions.length / 2
                ? 'Well done! You know your plants! üå±'
                : 'Keep learning about these amazing plants! üçÉ'}
            </p>
          </div>
          
          <button
            onClick={resetQuiz}
            className="bg-primary text-white w-full py-3 rounded-lg font-body font-medium transition-transform hover:scale-105"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }
  
  const currentQuestion = questions[currentQuestionIndex];
  
  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      <h1 className="text-2xl font-heading text-center text-primary mb-2">Plants Quiz</h1>
      <p className="text-center font-body text-earth-dark mb-6">
        Test your knowledge about Indian plants!
      </p>
      
      <div className="leaf-corner nature-bg p-6 rounded-lg shadow-natural">
        {/* Question Progress */}
        <div className="flex justify-between items-center mb-6">
          <span className="text-sm font-body text-earth-dark">
            Question {currentQuestionIndex + 1} of {questions.length}
          </span>
          <span className="text-sm font-medium font-body">
            Score: <span className="text-primary">{score}</span>
          </span>
        </div>
        
        {/* Progress Bar */}
        <div className="h-2 bg-gray-200 rounded-full overflow-hidden mb-6">
          <div 
            className="h-full bg-primary rounded-full transition-all duration-300"
            style={{ width: `${((currentQuestionIndex + 1) / questions.length) * 100}%` }}
          ></div>
        </div>
        
        {/* Question */}
        <h2 className="text-lg font-medium font-body mb-5 text-forest-dark">{currentQuestion.question}</h2>
        
        {/* Options */}
        <div className="space-y-3 mb-8">
          {currentQuestion.options.map((option, index) => (
            <button
              key={index}
              className={`w-full text-left p-4 rounded-lg border font-body transition-colors ${
                selectedOption === option
                  ? option === currentQuestion.correctAnswer
                    ? 'bg-green-100 border-green-500 text-green-800'
                    : 'bg-red-100 border-red-500 text-red-800'
                  : answered && option === currentQuestion.correctAnswer
                  ? 'bg-green-100 border-green-500 text-green-800'
                  : 'border-gray-300 hover:border-primary hover:bg-leaf-light/10'
              }`}
              onClick={() => handleOptionSelect(option)}
            >
              <div className="flex items-center">
                <span className="w-6 h-6 flex items-center justify-center rounded-full mr-3 bg-white border border-current text-sm">
                  {['A', 'B', 'C', 'D'][index]}
                </span>
                {option}
              </div>
            </button>
          ))}
        </div>
        
        {/* Next Button */}
        <button
          className={`w-full py-3 rounded-lg font-medium font-body ${
            answered
              ? 'bg-primary text-white transition-transform hover:scale-105'
              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
          }`}
          onClick={handleNextQuestion}
          disabled={!answered}
        >
          {currentQuestionIndex === questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
        </button>
      </div>
    </div>
  );
};

export default Quiz;
