import React from 'react';
import './App.css';
import './nature-theme.css';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Home from './pages/Home';
import Plants from './pages/Plants';
import PlantDetail from './pages/PlantDetail';
import About from './pages/About';
import NotFound from './pages/NotFound';
import { ThemeProvider } from './context/ThemeContext';
import ThemeToggle from './components/ThemeToggle';

function App() {
  return (
    <BrowserRouter>
      <ThemeProvider>
        <div className="App">
          <header>
            <div className="header-content">
              <h1>Nature Explorer</h1>
              <p>Discover the beauty of nature</p>
              <ThemeToggle />
            </div>
          </header>

          <main className="content">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/plants" element={<Plants />} />
              <Route path="/plants/:id" element={<PlantDetail />} />
              <Route path="/about" element={<About />} />
              <Route path="*" element={<NotFound />} />
            </Routes>
          </main>

          <nav className="bottom-nav">
            <Link to="/" className={window.location.pathname === '/' ? 'nav-item active-nav' : 'nav-item'}>
              <span className="material-icons-round">home</span>
              <span>Home</span>
            </Link>
            <Link to="/plants" className={window.location.pathname.startsWith('/plants') ? 'nav-item active-nav' : 'nav-item'}>
              <span className="material-icons-round">spa</span>
              <span>Plants</span>
            </Link>
            <Link to="/about" className={window.location.pathname === '/about' ? 'nav-item active-nav' : 'nav-item'}>
              <span className="material-icons-round">info</span>
              <span>About</span>
            </Link>
          </nav>
        </div>
      </ThemeProvider>
    </BrowserRouter>
  );
}

export default App;
