import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import plantsData from '../data/plants.json';

type Plant = {
  id: number;
  name: string;
  scientificName: string;
  type: string;
  image: string;
  shortDescription: string;
};

const Home: React.FC = () => {
  const [plants, setPlants] = useState<Plant[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('All');

  useEffect(() => {
    // Load plants data
    setPlants(plantsData);
  }, []);

  // Filter plants based on search term and type filter
  const filteredPlants = plants.filter((plant) => {
    // Filter by search term
    const matchesSearch = plant.name.toLowerCase().includes(searchTerm.toLowerCase());
    
    // Filter by type (if filter is not set to 'All')
    const matchesType = activeFilter === 'All' || plant.type === activeFilter;
    
    return matchesSearch && matchesType;
  });

  const filterTypes = ['All', 'Tree', 'Medicinal', 'Flower'];

  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      <h1 className="text-3xl font-bold text-primary text-center mb-2">Nature Explorer</h1>
      <p className="text-center mb-6 text-gray-600">Discover the amazing plants of India</p>
      
      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search plants..."
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      
      {/* Filter Buttons */}
      <div className="flex flex-wrap gap-2 mb-6">
        {filterTypes.map((type) => (
          <button
            key={type}
            className={`px-4 py-2 rounded-full ${
              activeFilter === type
                ? 'bg-primary text-white'
                : 'bg-gray-200 text-gray-700'
            }`}
            onClick={() => setActiveFilter(type)}
          >
            {type}
          </button>
        ))}
      </div>
      
      {/* Plants Grid */}
      {filteredPlants.length > 0 ? (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredPlants.map((plant) => (
            <Link to={`/plant/${plant.id}`} key={plant.id} className="plant-card bg-white">
              <div className="aspect-square overflow-hidden">
                <img
                  src={plant.image}
                  alt={plant.name}
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="p-3">
                <h2 className="font-bold text-lg text-primary">{plant.name}</h2>
                <p className="text-xs text-gray-600 italic mb-1">{plant.scientificName}</p>
                <p className="text-sm text-gray-800 line-clamp-2">{plant.shortDescription}</p>
              </div>
            </Link>
          ))}
        </div>
      ) : (
        <div className="text-center py-10">
          <p className="text-gray-500">No plants found matching your criteria.</p>
        </div>
      )}
    </div>
  );
};

export default Home;
