import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import './App.css';
import './nature-theme.css';

// Pages
import Home from './components/Home';
import Quiz from './components/Quiz';
import About from './components/About';
import PlantDetail from './components/PlantDetail';

function App() {
  return (
    <Router>
      <div className="App min-h-screen pb-24 px-4 mx-auto">
        <header className="py-6 relative">
          <div className="leaf-corner container mx-auto px-6 py-4">
            <h1 className="text-3xl font-heading text-center text-forest-dark">
              <span className="inline-block mr-2">üåø</span>
              Nature Explorer
            </h1>
            <p className="text-center text-earth-dark text-sm mt-2 font-body">
              Discover the amazing plants of India
            </p>

            <div className="absolute top-0 left-0 w-12 h-12 pointer-events-none opacity-40">
              <svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
                <path d="M0,0 C50,25 75,50 100,100 L0,100 Z" fill="#4CAF50" fillOpacity="0.3" />
              </svg>
            </div>

            <div className="absolute top-0 right-0 w-12 h-12 pointer-events-none opacity-40">
              <svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
                <path d="M100,0 C50,25 25,50 0,100 L100,100 Z" fill="#4CAF50" fillOpacity="0.3" />
              </svg>
            </div>
          </div>
        </header>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/plant/:id" element={<PlantDetail />} />
          <Route path="/quiz" element={<Quiz />} />
          <Route path="/about" element={<About />} />
        </Routes>

        <BottomNavigation />
      </div>
    </Router>
  );
}

function BottomNavigation() {
  const location = useLocation();

  return (
    <nav className="bottom-nav">
      <Link to="/" className={`nav-item ${location.pathname === '/' ? 'active-nav' : ''}`}>
        <span className="text-xl">üåø</span>
        <span className="text-xs font-body">Home</span>
      </Link>
      <Link to="/quiz" className={`nav-item ${location.pathname === '/quiz' ? 'active-nav' : ''}`}>
        <span className="text-xl">üß†</span>
        <span className="text-xs font-body">Quiz</span>
      </Link>
      <Link to="/about" className={`nav-item ${location.pathname === '/about' ? 'active-nav' : ''}`}>
        <span className="text-xl">‚ÑπÔ∏è</span>
        <span className="text-xs font-body">About</span>
      </Link>
    </nav>
  );
}

export default App;
