import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import plantsData from '../data/plants.json';

type PlantParams = {
  id: string;
};

type Plant = {
  id: number;
  name: string;
  scientificName: string;
  type: string;
  image: string;
  shortDescription: string;
  description: {
    english: string;
    hindi: string;
  };
  uses: string[];
};

const PlantDetail: React.FC = () => {
  const { id } = useParams<PlantParams>();
  const navigate = useNavigate();
  const [plant, setPlant] = useState<Plant | null>(null);
  const [language, setLanguage] = useState<'english' | 'hindi'>('english');
  
  useEffect(() => {
    // Find the plant by ID
    const plantById = plantsData.find((p) => p.id === Number(id));
    
    if (plantById) {
      setPlant(plantById);
    }
  }, [id]);
  
  if (!plant) {
    return (
      <div className="container mx-auto px-4 py-8 text-center">
        <p className="text-gray-600">Plant not found.</p>
        <button 
          onClick={() => navigate('/')}
          className="mt-4 bg-primary text-white px-6 py-2 rounded-lg"
        >
          Back to Home
        </button>
      </div>
    );
  }
  
  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        {/* Back Button */}
        <div className="p-4">
          <button 
            onClick={() => navigate('/')}
            className="flex items-center text-primary"
          >
            <span>←</span>
            <span className="ml-2">Back</span>
          </button>
        </div>
        
        {/* Plant Image */}
        <div className="w-full h-48 md:h-64 overflow-hidden">
          <img 
            src={plant.image} 
            alt={plant.name} 
            className="w-full h-full object-cover"
          />
        </div>
        
        {/* Plant Info */}
        <div className="p-4">
          <div className="flex justify-between items-start mb-2">
            <div>
              <h1 className="text-2xl font-bold text-primary">{plant.name}</h1>
              <p className="text-sm italic text-gray-600">{plant.scientificName}</p>
            </div>
            <span className="px-3 py-1 bg-secondary text-white rounded-full text-sm">
              {plant.type}
            </span>
          </div>
          
          {/* Language Switcher */}
          <div className="flex gap-2 mt-4 mb-2">
            <button
              className={`px-3 py-1 text-sm border rounded-full ${
                language === 'english' 
                  ? 'bg-primary text-white border-primary' 
                  : 'bg-white text-gray-700 border-gray-300'
              }`}
              onClick={() => setLanguage('english')}
            >
              English
            </button>
            <button
              className={`px-3 py-1 text-sm border rounded-full ${
                language === 'hindi' 
                  ? 'bg-primary text-white border-primary' 
                  : 'bg-white text-gray-700 border-gray-300'
              }`}
              onClick={() => setLanguage('hindi')}
            >
              हिंदी
            </button>
          </div>
          
          {/* Description */}
          <div className="mb-4">
            <h2 className="text-lg font-semibold mb-2">Description</h2>
            <p className="text-gray-700">
              {plant.description[language]}
            </p>
          </div>
          
          {/* Uses */}
          <div>
            <h2 className="text-lg font-semibold mb-2">Uses</h2>
            <ul className="list-disc list-inside">
              {plant.uses.map((use, index) => (
                <li key={index} className="text-gray-700 mb-1">{use}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PlantDetail;
