import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import plantsData from '../data/plants.json';
import '../nature-theme.css';

type Plant = {
  id: number;
  name: string;
  scientificName: string;
  type: string;
  image: string;
  shortDescription: string;
};

const Home: React.FC = () => {
  const [plants, setPlants] = useState<Plant[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('All');

  useEffect(() => {
    // Load plants data
    setPlants(plantsData);
  }, []);

  // Filter plants based on search term and type filter
  const filteredPlants = plants.filter((plant) => {
    // Filter by search term
    const matchesSearch = plant.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                          plant.scientificName.toLowerCase().includes(searchTerm.toLowerCase());
    
    // Filter by type (if filter is not set to 'All')
    const matchesType = activeFilter === 'All' || plant.type === activeFilter;
    
    return matchesSearch && matchesType;
  });

  const filterTypes = ['All', 'Tree', 'Medicinal', 'Flower'];

  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      {/* Search Section */}
      <div className="leaf-border mb-6">
        <div className="relative mb-4">
          <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
            <span className="text-green-600">üîç</span>
          </div>
          <input
            type="text"
            className="search-input"
            placeholder="Search plants..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        
        {/* Filter Buttons */}
        <div className="flex flex-wrap gap-2">
          <button
            className={`filter-btn ${activeFilter === 'all' ? 'active' : ''}`}
            onClick={() => setActiveFilter('all')}
          >
            All Plants
          </button>
          <button
            className={`filter-btn ${activeFilter === 'tree' ? 'active' : ''}`}
            onClick={() => setActiveFilter('tree')}
          >
            üå≥ Trees
          </button>
          <button
            className={`filter-btn ${activeFilter === 'flower' ? 'active' : ''}`}
            onClick={() => setActiveFilter('flower')}
          >
            üå∏ Flowers
          </button>
          <button
            className={`filter-btn ${activeFilter === 'medicinal' ? 'active' : ''}`}
            onClick={() => setActiveFilter('medicinal')}
          >
            üåø Medicinal
          </button>
        </div>
      </div>
      
      {/* Plants Grid */}
      {filteredPlants.length === 0 ? (
        <div className="leaf-corner p-8 text-center">
          <span className="text-5xl mb-4 block">üçÉ</span>
          <h2 className="text-2xl font-heading text-forest-dark mb-2">No plants found</h2>
          <p className="font-body text-earth-dark">Try adjusting your search or filter criteria.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredPlants.map((plant) => (
            <Link to={`/plant/${plant.id}`} key={plant.id} className="plant-card">
              <div className="h-48 overflow-hidden relative">
                <img 
                  src={plant.image} 
                  alt={plant.name} 
                  className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                />
                <div className="absolute top-2 right-2 bg-leaf-dark text-white text-xs px-2 py-1 rounded-full font-body">
                  {plant.type}
                </div>
              </div>
              <div className="p-4">
                <h3 className="font-heading text-forest-dark text-lg mb-1">{plant.name}</h3>
                <p className="font-serif text-sm italic text-earth-dark/80 mb-2">{plant.scientificName}</p>
                <p className="font-body text-earth-dark text-sm line-clamp-2">{plant.shortDescription}</p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
