import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import './App.css';
import './nature-theme.css';

// Pages
import Home from './components/Home';
import Quiz from './components/Quiz';
import About from './components/About';
import PlantDetail from './components/PlantDetail';

// Define theme type
type Theme = 'light' | 'dark';

function App() {
  // Theme state
  const [theme, setTheme] = useState<Theme>(() => {
    // Get theme from localStorage if available
    const savedTheme = localStorage.getItem('nature-explorer-theme');
    // Check if system prefers dark mode
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;

    return (savedTheme as Theme) || (prefersDark ? 'dark' : 'light');
  });

  // Apply theme when it changes
  useEffect(() => {
    // Save theme to localStorage
    localStorage.setItem('nature-explorer-theme', theme);

    // Apply theme to document
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  // Toggle theme function
  const toggleTheme = () => {
    setTheme(prevTheme => prevTheme === 'light' ? 'dark' : 'light');
  };

  return (
    <Router>
      <div className="App min-h-screen pb-24 px-4 mx-auto">
        <header className="py-6 relative">
          <div className="leaf-corner container mx-auto px-6 py-4">
            <h1 className="text-3xl font-heading text-center text-forest-dark">
              <span className="inline-block mr-2">üåø</span>
              Nature Explorer
            </h1>
            <p className="text-center text-earth-dark text-sm mt-2 font-body">
              Discover the amazing plants of India
            </p>

            {/* Theme Toggle Button */}
            <button
              onClick={toggleTheme}
              className="theme-toggle-btn absolute top-4 right-4"
              aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
            >
              {theme === 'light' ? (
                <span className="flex items-center">
                  <svg 
                    xmlns="http://www.w3.org/2000/svg" 
                    className="w-5 h-5 mr-2" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor" 
                    strokeWidth="2" 
                    strokeLinecap="round" 
                    strokeLinejoin="round"
                  >
                    <path d="M21 12.79A9 9 0 1111.21 3 7 7 0 0021 12.79z"></path>
                  </svg>
                  Dark
                </span>
              ) : (
                <span className="flex items-center">
                  <svg 
                    xmlns="http://www.w3.org/2000/svg" 
                    className="w-5 h-5 mr-2" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor" 
                    strokeWidth="2" 
                    strokeLinecap="round" 
                    strokeLinejoin="round"
                  >
                    <circle cx="12" cy="12" r="5"></circle>
                    <line x1="12" y1="1" x2="12" y2="3"></line>
                    <line x1="12" y1="21" x2="12" y2="23"></line>
                    <line x1="4.22" y1="4.22" x2="5.64" y2="5.64"></line>
                    <line x1="18.36" y1="18.36" x2="19.78" y2="19.78"></line>
                    <line x1="1" y1="12" x2="3" y2="12"></line>
                    <line x1="21" y1="12" x2="23" y2="12"></line>
                    <line x1="4.22" y1="19.78" x2="5.64" y2="18.36"></line>
                    <line x1="18.36" y1="5.64" x2="19.78" y2="4.22"></line>
                  </svg>
                  Light
                </span>
              )}
            </button>

            <div className="absolute top-0 left-0 w-12 h-12 pointer-events-none opacity-40">
              <svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
                <path d="M0,0 C50,25 75,50 100,100 L0,100 Z" fill="#4CAF50" fillOpacity="0.3" />
              </svg>
            </div>

            <div className="absolute top-0 right-0 w-12 h-12 pointer-events-none opacity-40">
              <svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
                <path d="M100,0 C50,25 25,50 0,100 L100,100 Z" fill="#4CAF50" fillOpacity="0.3" />
              </svg>
            </div>
          </div>
        </header>

        <Routes>
          <Route path="/" element={<Home themeProp={{ theme, toggleTheme }} />} />
          <Route path="/plant/:id" element={<PlantDetail />} />
          <Route path="/quiz" element={<Quiz />} />
          <Route path="/about" element={<About />} />
        </Routes>

        <BottomNavigation />
      </div>
    </Router>
  );
}

function BottomNavigation() {
  const location = useLocation();

  return (
    <nav className="bottom-nav">
      <Link to="/" className={`nav-item ${location.pathname === '/' ? 'active-nav' : ''}`}>
        <span className="text-xl">üåø</span>
        <span className="text-xs font-body">Home</span>
      </Link>
      <Link to="/quiz" className={`nav-item ${location.pathname === '/quiz' ? 'active-nav' : ''}`}>
        <span className="text-xl">üß†</span>
        <span className="text-xs font-body">Quiz</span>
      </Link>
      <Link to="/about" className={`nav-item ${location.pathname === '/about' ? 'active-nav' : ''}`}>
        <span className="text-xl">‚ÑπÔ∏è</span>
        <span className="text-xs font-body">About</span>
      </Link>
    </nav>
  );
}

export default App;
