import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import plantsData from '../data/plants.json';

type Plant = {
  id: number;
  name: string;
  scientificName: string;
  type: string;
  image: string;
  shortDescription: string;
};

const Home: React.FC = () => {
  const [plants, setPlants] = useState<Plant[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('All');

  useEffect(() => {
    // Load plants data
    setPlants(plantsData);
  }, []);

  // Filter plants based on search term and type filter
  const filteredPlants = plants.filter((plant) => {
    // Filter by search term
    const matchesSearch = plant.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                          plant.scientificName.toLowerCase().includes(searchTerm.toLowerCase());
    
    // Filter by type (if filter is not set to 'All')
    const matchesType = activeFilter === 'All' || plant.type === activeFilter;
    
    return matchesSearch && matchesType;
  });

  const filterTypes = ['All', 'Tree', 'Medicinal', 'Flower'];

  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      <h1 className="sr-only">Nature Explorer</h1>
      <p className="text-center mb-6 font-body text-forest-dark">Discover the amazing plants of India</p>
      
      {/* Search Bar */}
      <div className="mb-6">
        <div className="relative">
          <span className="absolute left-3 top-1/2 -translate-y-1/2 text-lg">üîç</span>
          <input
            type="text"
            placeholder="Search plants..."
            className="w-full p-3 pl-10 border border-leaf-light rounded-lg focus:outline-none focus:ring-2 focus:ring-primary font-body shadow-sm"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>
      
      {/* Filter Buttons */}
      <div className="flex flex-wrap gap-2 mb-6">
        {filterTypes.map((type) => (
          <button
            key={type}
            className={`px-4 py-2 rounded-full font-body transition-colors ${
              activeFilter === type
                ? 'bg-primary text-white shadow-sm'
                : 'bg-white text-earth-dark border border-leaf-light hover:bg-leaf-light/20'
            }`}
            onClick={() => setActiveFilter(type)}
          >
            {type}
          </button>
        ))}
      </div>
      
      {/* Plants Grid */}
      {filteredPlants.length > 0 ? (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-5">
          {filteredPlants.map((plant) => (
            <Link to={`/plant/${plant.id}`} key={plant.id} className="plant-card nature-bg">
              <div className="aspect-square overflow-hidden relative">
                <div className="absolute inset-0 bg-gradient-to-b from-transparent to-black/20 z-10"></div>
                <img
                  src={plant.image}
                  alt={plant.name}
                  className="w-full h-full object-cover transition-transform hover:scale-110 duration-300"
                />
                <span className="absolute top-2 right-2 px-2 py-1 bg-leaf-dark/80 text-white rounded-full text-xs font-body z-20">
                  {plant.type}
                </span>
              </div>
              <div className="p-4">
                <h2 className="font-heading text-xl text-primary leading-tight">{plant.name}</h2>
                <p className="text-xs font-serif text-earth-dark italic mb-2">{plant.scientificName}</p>
                <p className="text-sm font-body text-earth-dark line-clamp-2">{plant.shortDescription}</p>
              </div>
            </Link>
          ))}
        </div>
      ) : (
        <div className="text-center py-10 border border-dashed border-leaf-light rounded-lg p-8">
          <span className="text-4xl block mb-2">üçÉ</span>
          <p className="text-earth-dark font-body">No plants found matching your criteria.</p>
          <button 
            onClick={() => { setSearchTerm(''); setActiveFilter('All'); }}
            className="mt-4 text-primary font-body underline"
          >
            Clear filters
          </button>
        </div>
      )}
    </div>
  );
};

export default Home;
