import React, { useState, useEffect } from 'react';
import quizData from '../data/quiz.json';

type Question = {
  id: number;
  question: string;
  options: string[];
  correctAnswer: string;
};

const Quiz: React.FC = () => {
  const [questions, setQuestions] = useState<Question[]>([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState<string | null>(null);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [answered, setAnswered] = useState(false);
  
  useEffect(() => {
    // Load quiz data
    setQuestions(quizData);
  }, []);
  
  const handleOptionSelect = (option: string) => {
    if (answered) return;
    
    setSelectedOption(option);
    setAnswered(true);
    
    // Check if answer is correct
    if (option === questions[currentQuestionIndex].correctAnswer) {
      setScore(score + 1);
    }
  };
  
  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedOption(null);
      setAnswered(false);
    } else {
      setShowResult(true);
    }
  };
  
  const resetQuiz = () => {
    setCurrentQuestionIndex(0);
    setSelectedOption(null);
    setScore(0);
    setShowResult(false);
    setAnswered(false);
  };
  
  if (questions.length === 0) {
    return <div className="text-center py-10">Loading quiz...</div>;
  }
  
  if (showResult) {
    return (
      <div className="container mx-auto px-4 pt-8 pb-20">
        <div className="bg-white p-6 rounded-lg shadow-md max-w-md mx-auto">
          <h1 className="text-2xl font-bold text-center text-primary mb-6">Quiz Results</h1>
          
          <div className="text-center mb-6">
            <div className="text-6xl font-bold mb-2">
              {score}/{questions.length}
            </div>
            <p className="text-gray-600">
              {score === questions.length
                ? 'Perfect score! You\'re a plant expert!'
                : score >= questions.length / 2
                ? 'Well done! You know your plants!'
                : 'Keep learning about these amazing plants!'}
            </p>
          </div>
          
          <button
            onClick={resetQuiz}
            className="bg-primary text-white w-full py-3 rounded-lg font-medium"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }
  
  const currentQuestion = questions[currentQuestionIndex];
  
  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      <h1 className="text-2xl font-bold text-center text-primary mb-4">Plants Quiz</h1>
      <p className="text-center text-gray-600 mb-6">
        Test your knowledge about Indian plants!
      </p>
      
      <div className="bg-white p-6 rounded-lg shadow-md">
        {/* Question Progress */}
        <div className="flex justify-between items-center mb-4">
          <span className="text-sm text-gray-600">
            Question {currentQuestionIndex + 1} of {questions.length}
          </span>
          <span className="text-sm font-medium">
            Score: {score}
          </span>
        </div>
        
        {/* Question */}
        <h2 className="text-lg font-medium mb-4">{currentQuestion.question}</h2>
        
        {/* Options */}
        <div className="space-y-3 mb-6">
          {currentQuestion.options.map((option, index) => (
            <button
              key={index}
              className={`w-full text-left p-3 rounded-lg border ${
                selectedOption === option
                  ? option === currentQuestion.correctAnswer
                    ? 'bg-green-100 border-green-500'
                    : 'bg-red-100 border-red-500'
                  : answered && option === currentQuestion.correctAnswer
                  ? 'bg-green-100 border-green-500'
                  : 'border-gray-300 hover:border-primary'
              }`}
              onClick={() => handleOptionSelect(option)}
            >
              {option}
            </button>
          ))}
        </div>
        
        {/* Next Button */}
        <button
          className={`w-full py-3 rounded-lg font-medium ${
            answered
              ? 'bg-primary text-white'
              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
          }`}
          onClick={handleNextQuestion}
          disabled={!answered}
        >
          {currentQuestionIndex === questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
        </button>
      </div>
    </div>
  );
};

export default Quiz;
