import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import plantsData from '../data/plants.json';

type PlantParams = {
  id: string;
};

type Plant = {
  id: number;
  name: string;
  scientificName: string;
  type: string;
  image: string;
  shortDescription: string;
  description: {
    english: string;
    hindi: string;
  };
  uses: string[];
};

const PlantDetail: React.FC = () => {
  const { id } = useParams<PlantParams>();
  const navigate = useNavigate();
  const [plant, setPlant] = useState<Plant | null>(null);
  const [language, setLanguage] = useState<'english' | 'hindi'>('english');
  
  useEffect(() => {
    // Find the plant by ID
    const plantById = plantsData.find((p) => p.id === Number(id));
    
    if (plantById) {
      setPlant(plantById);
    }
  }, [id]);
  
  if (!plant) {
    return (
      <div className="container mx-auto px-4 py-8 text-center">
        <p className="text-gray-600 font-body">Plant not found.</p>
        <button 
          onClick={() => navigate('/')}
          className="mt-4 bg-primary text-white px-6 py-2 rounded-lg font-body"
        >
          Back to Home
        </button>
      </div>
    );
  }
  
  return (
    <div className="container mx-auto px-4 pt-4 pb-20">
      <div className="leaf-border nature-bg rounded-lg shadow-natural overflow-hidden">
        {/* Back Button */}
        <div className="p-4">
          <button 
            onClick={() => navigate('/')}
            className="flex items-center text-primary font-body"
          >
            <span>←</span>
            <span className="ml-2">Back</span>
          </button>
        </div>
        
        {/* Plant Image */}
        <div className="w-full h-56 md:h-72 overflow-hidden relative">
          <div className="absolute inset-0 bg-gradient-to-b from-transparent to-black/20 z-10"></div>
          <img 
            src={plant.image} 
            alt={plant.name} 
            className="w-full h-full object-cover"
          />
        </div>
        
        {/* Plant Info */}
        <div className="p-6">
          <div className="flex justify-between items-start mb-4">
            <div>
              <h1 className="text-3xl font-heading text-primary">{plant.name}</h1>
              <p className="text-sm italic font-serif text-earth-dark">{plant.scientificName}</p>
            </div>
            <span className="px-3 py-1 bg-leaf-dark text-white rounded-full text-sm font-body">
              {plant.type}
            </span>
          </div>
          
          {/* Language Switcher */}
          <div className="flex gap-3 mt-6 mb-4">
            <button
              className={`px-4 py-2 text-sm border rounded-full font-body transition-colors ${
                language === 'english' 
                  ? 'bg-primary text-white border-primary' 
                  : 'bg-white text-earth-dark border-earth-light hover:bg-leaf-light/20'
              }`}
              onClick={() => setLanguage('english')}
            >
              English
            </button>
            <button
              className={`px-4 py-2 text-sm border rounded-full font-body transition-colors ${
                language === 'hindi' 
                  ? 'bg-primary text-white border-primary' 
                  : 'bg-white text-earth-dark border-earth-light hover:bg-leaf-light/20'
              }`}
              onClick={() => setLanguage('hindi')}
            >
              हिंदी
            </button>
          </div>
          
          {/* Description */}
          <div className="mb-6">
            <h2 className="text-2xl font-heading text-forest-dark mb-3">Description</h2>
            <p className="font-body text-earth-dark leading-relaxed">
              {plant.description[language]}
            </p>
          </div>
          
          {/* Uses */}
          <div>
            <h2 className="text-2xl font-heading text-forest-dark mb-3">Uses</h2>
            <ul className="space-y-2">
              {plant.uses.map((use, index) => (
                <li key={index} className="flex items-start">
                  <span className="mr-2 text-accent">•</span>
                  <span className="font-body text-earth-dark">{use}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PlantDetail;
