import React from 'react';
import { useParams, Link } from 'react-router-dom';

const PlantDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  
  // Mock plant data
  const plants = [
    { id: 1, name: 'Monstera Deliciosa', type: 'Indoor', description: 'Known for its large, glossy, Swiss cheese-like leaves. Easy to care for and thrives in bright, indirect light.' },
    { id: 2, name: 'Snake Plant', type: 'Indoor', description: 'A hardy plant that can survive in low light conditions and with minimal watering. Great for purifying the air.' },
    { id: 3, name: 'Fiddle Leaf Fig', type: 'Indoor', description: 'Popular for its large, violin-shaped leaves. Requires consistent care and prefers bright, filtered light.' },
    { id: 4, name: 'Aloe Vera', type: 'Succulent', description: 'Known for its medicinal properties. Thrives in bright light and needs infrequent watering.' },
    { id: 5, name: 'Lavender', type: 'Outdoor', description: 'Fragrant herb with purple flowers. Needs full sun and well-drained soil.' },
    { id: 6, name: 'Rosemary', type: 'Herb', description: 'Aromatic herb used in cooking. Requires full sun and minimal watering.' }
  ];
  
  const plant = plants.find(p => p.id === parseInt(id || '0'));
  
  if (!plant) {
    return (
      <div className="plant-detail">
        <h1>Plant Not Found</h1>
        <p>The plant you're looking for doesn't exist in our collection.</p>
        <Link to="/plants" className="back-link">Back to Plants</Link>
      </div>
    );
  }
  
  return (
    <div className="plant-detail">
      <h1>{plant.name}</h1>
      <div className="plant-info card">
        <p><strong>Type:</strong> {plant.type}</p>
        <p>{plant.description}</p>
      </div>
      <Link to="/plants" className="back-link">Back to Plants</Link>
    </div>
  );
};

export default PlantDetail;
